import { useState } from 'react';
import useGetSubmits from '../api/useGetSubmits';
import Item from '../components/Item';
import '../css/monitor.css';

function Monitor() {

    const [rerender, setRerender] = useState(true);
    const [data, isLoading, error] = useGetSubmits(rerender);

    function deleted(id, msg) {
        console.log(id, msg);set
        // setRerender((cur) => {return !cur});
        data.filter((item) => { 
            return item.id !== id;
        })
    };

    return (
        <main>
            <Item
                commandId="identifiant de commande"
                siteName="Raison sociale du site"
                managerName="Nom du gerant"
                operatorName="Nom du l'operateur"> </Item>
            {isLoading && <div>loading...</div>}
            {error && <div>error...</div>}
            {!isLoading && data && data.map((item) => {
                return <Item
                    key={item.id}
                    commandId={item.id}
                    siteName={item.siteName}
                    managerName={item.maFullName}
                    operatorName={item.opFullName}
                    deleted={deleted}
                />
            })}
        </main>
    );
}

export default Monitor;
