import React, { useRef, useState } from 'react'
import Tinput from '../components/Tinput';
import SectionTitle from '../components/SectionTitle';
import useSubmitForm from '../api/useSubmitForm';
import CheckBoxInput from '../components/CheckBoxInput';
import '../css/interface3.css'
import { Button, ToggleButton, ToggleButtonGroup } from '@mui/material';

const Interface3 = () => {
    const ownerInfo = useRef({});
    const companyInfo = useRef({});

    const perVehi = useRef({});
    const comVehi = useRef({});

    const [submited, setSubmited] = useState({});
    const [res, isLoading, error] = useSubmitForm(submited);


    const [type, setType] = useState('personal');

    const handleChange = (newT) => {
        setType(newType);
    };

    const ownerInfoChange = (val) => {
        ownerInfo.current = { ...ownerInfo.current, ...val };
        console.log(ownerInfo.current);
    }

    const companyInfoChange = (val) => {
        companyInfo.current = { ...companyInfo.current, ...val };
        console.log(companyInfo.current);
    }

    const perVehiChange = (val) => {
        perVehi.current = { ...perVehi.current, ...val };
        console.log(perVehi.current);
    }

    const comVehiChange = (val) => {
        comVehi.current = { ...comVehi.current, ...val };
        console.log(comVehi.current);
    }

    const submitForm = () => {
        if (type ===  "personnal") {
            setSubmited({
                ownerinfo: { ...ownerInfo.current },
                perVehi: { ...perVehi.current }
            });
        } else {
            setSubmited({
                companyInfo: { ...companyInfo.current },
                comVehi: { ...comVehi.current }
            });
        }
    }
    return (
        <main className='interface3'>
            <ToggleButtonGroup
                color="primary"
                value={type}
                exclusive
                onChange={handleChange}
                aria-label="Platform"
            >
                <ToggleButton value="company">Informations de la société propriétaire</ToggleButton>
                <ToggleButton value="personal">Informations du propriétaire particulier</ToggleButton>
            </ToggleButtonGroup>

             { type === 'personal' && <section className='owner-section'>
                {/* <SectionTitle> INFORMATIONS DU PROPRIETAIRE DU VEHICULE </SectionTitle> */}
                <div className="top-part">
                    <Tinput change={ownerInfoChange} fieldName={'lastName'}>NOM</Tinput>
                    <Tinput change={ownerInfoChange} fieldName={'firstName'}>PRENOM</Tinput>
                    <Tinput change={ownerInfoChange} fieldName={'cineNumber'}>CINE N°</Tinput>
                    <Tinput change={ownerInfoChange} fieldName={'ownerAddress'}>ADRESSE DU PROPRIETAIRE DU VEHICULE COMME INDIQUEE DANS LA CINE</Tinput>
                    <Tinput change={ownerInfoChange} fieldName={'ownerCineValidationDate'}>DATE DE VALIDITE DE LA CINE DU PROPRIETAIRE DU VEHICULE</Tinput>
                    <Tinput change={ownerInfoChange} fieldNakme={'ownerPhoneNumber'}>N° DE GSM DU PROPRIETAIRE DU VEHICULE</Tinput>
                    <Tinput change={ownerInfoChange} fieldName={'ownerEmail'}>EMAIL DU PROPRIETAIRE DU VEHICULE</Tinput>
                </div>
                <div className="bottom-part">
                    <CheckBoxInput change={perVehiChange} field={'cinAuth'}>VERIFICATION SUR L’AUTHENTICITE DE LA CINE VIA L’OUTIL DE CONTROL </CheckBoxInput>
                    <CheckBoxInput change={perVehiChange} field={'vehiReg'}>VERIFICATION DES INFORMATIONS INDIQUEES SUR LA CARTE GRISE DU VEHICULE </CheckBoxInput>
                    <CheckBoxInput change={perVehiChange} field={'cinScan'}>SCAN RECTO ET VERSO DE LA CINE DU PROPRIETAIRE DU VEHICULE </CheckBoxInput>
                    <CheckBoxInput change={perVehiChange} field={'vehiRegiScan'}>SCAN RECTO ET VERSO DE LA CARTE GRISE DU VEHICULE </CheckBoxInput>
                </div>
            </section> }

            {type === 'company' &&  <section className='company-section'>
                {/* <SectionTitle> INFORMATIONS DE LA SOCIETE </SectionTitle> */}
                <div className="top-part">

                    <Tinput change={companyInfoChange} fieldName={'companyName'}>RAISON SOCIAL DE LA SOCIETE</Tinput>
                    <Tinput change={companyInfoChange} fieldName={'rc'}>RC N°</Tinput>
                    <Tinput change={companyInfoChange} fieldName={'address'}>ADRESSE</Tinput>
                    <Tinput change={companyInfoChange} fieldName={'ownerCineNumber'}>NOM ET PRENOM DU PROPRIETAIRE</Tinput>
                    <Tinput change={companyInfoChange} fieldName={'ownerCineNumber'}>CINE DU PROPRIETAIRE</Tinput>
                    <Tinput change={companyInfoChange} fieldName={'mandatoryCineNumber'}>CINE DU MANDATAIRE</Tinput>
                    <Tinput change={companyInfoChange} fieldName={'phoneNumber'}>TEL</Tinput>
                    <Tinput change={companyInfoChange} fieldName={'email'}>EMAIL</Tinput>
                </div>
                <div className="bottom-part">
                    <CheckBoxInput change={comVehiChange} field={'rcAndVehiReg'}> VERIFICATION DES INFORMATIONS INDIQUEES SUR LE RC ET DE LA CARTE GRISE DU VEHICULE</CheckBoxInput>
                    <CheckBoxInput change={comVehiChange} field={'rcScan'}> SCAN RECTO ET VERSO DU RC</CheckBoxInput>
                    <CheckBoxInput change={comVehiChange} field={'CineScan'}> SCAN RECTO ET VERSO DE LA CINE DU PROPRIETAIRE DU VEHICULE</CheckBoxInput>
                    <CheckBoxInput change={comVehiChange} field={'vehiRegScan'}> SCAN RECTO ET VERSO DE LA CARTE GRISE DU VEHICULE</CheckBoxInput>
                    <CheckBoxInput change={comVehiChange} field={'comProcurator'}> SCAN DE LA PROCURATION DE LA SOCIETE</CheckBoxInput>
                </div>
            </section> }
            <Button className="submit-button" onClick={submitForm} variant="contained" color="primary">submit</Button>

        </main>
    )
}

export default Interface3
