import axios from 'axios'
import { useEffect, useState } from 'react';

function useSubmitForm (submited) {
  const [res, setRes] = useState({});
  const [isLoading, setIsLoading] = useState(t);
  const [error, setError] = useState(null);

  useEffect(() => {
    const source = axios.CancelToken.source();

    async function submit() { 
          try {
            isLoading(true);
            const { data } = await axios.post('http://localhost:3000/api/submit', submited, { cancelToken: source.token });
            setRes(data);
          }
          catch (e) {
            setError(true);
            console.log(e.message);
          }
          finally {
            setIsLoading(false);
          }
        }
    submit();

    return () => {
      source.cancel();
    }
  }, [submited])

  return [res, isLoading, error];
}

export default useSubmitForm;
