import { useRef, useState } from 'react';
import '../App.css';
import Tinput from '../components/Tinput';
import SectionTitle from '../components/SectionTitle';
import useSubmitForm from '../api/useSubmitForm';
import { Button } from '@mui/material';




function Interface1() {
  const siteInfo = useRef({});
  const managerInfo = useRef({});
  const operatorInfo = useRef({});
  const [stepper, setStepper] = useState(0); 
  const [submit, setSubmit] = useState({});
  const [res, isLoading, error] = useSubmitForm(submit);

  function siteInfoChange(value) {
    siteInfo.current = { ...siteInfo.current, ...value };
    console.log(siteInfo.current);
  }

  function managerInfoChange(value) {
    managerInfo.current = { ...managerInfo.current, ...value };
  }

  function operatorInfoChange(value) {
    operatorInfo.current = { ...operatorInfo.current, ...value };
  }

  function submitForm(e) {
    e.preventDefault();
    e.stopPropagation();
    setSubmit({ 
      siteSection: {...siteInfo.current},
      managerSection: {...managerInfo.current},
      operatorSection: {...operatorInfo.current}})
  }

  return (
    <main className="section1">
      { stepper == 0 && <section className='site-section'>
        <SectionTitle className="section-title">INFORMATIONS SUR LE SITE</SectionTitle>
        <Tinput change={siteInfoChange} fieldName={'siteCode'}>CODE D’IDENTIFICATION DU SITE N°</Tinput>
        <Tinput change={siteInfoChange} fieldName={'comName'}>RAISON SOCIALE DU SITE</Tinput>
        <Tinput change={siteInfoChange} fieldName={'siteAddress'}>ADRESSE DU SITE</Tinput>
        <div className='bottom-flex'>
          <Tinput change={siteInfoChange} fieldName={'patente'}>PATENTE N°</Tinput>
          <Tinput change={siteInfoChange} fieldName={'rcNumber'}> RC N°</Tinput>
          <Tinput change={siteInfoChange} fieldName={'iceNumber'}>ICE N°</Tinput>
          <Tinput change={siteInfoChange} fieldName={'atel'}>ATEL</Tinput>
        </div>
      </section> 
      }
{/* 
      <section className="manager-section">
        <SectionTitle>2-INFORMATIONS SUR LE GERANT DU SITE</SectionTitle>
        <Tinput change={managerInfoChange} fieldName={'fullName'}>2-NOM ET PRENOM DU GERANT DU SITE</Tinput>
        <Tinput change={managerInfoChange} fieldName={'cineNumber'}>3-N° CINE DU GERANT DU SITE</Tinput>
        <Tinput change={managerInfoChange} fieldName={'address'}>4-ADRESSE DU GERANT DU SITE</Tinput>
        <Tinput change={managerInfoChange} fieldName={'mobileNumber'}>5-N° DE GSM DU GERANT DU SITE</Tinput>
        <Tinput change={managerInfoChange} fieldName={'email'}>5-N° DE GSM DU GERANT DU SITE</Tinput>
      </section>

      <section className="operator-info">
        <SectionTitle>3-INFORMATIONS SUR L’OPERATEUR DE SAISIE</SectionTitle>
        <Tinput change={operatorInfoChange} fieldName={'code'}>1-CODE OPERATEUR DE SAISIE</Tinput>
        <Tinput change={operatorInfoChange} fieldName={'fullName'}>2-NOM ET PRENOM DE L’OPERATEUR DE SAISIE</Tinput>
        <Tinput change={operatorInfoChange} fieldName={'cineNumber'}>3-N° CINE DE L’OPERATEUR DE SAISIE</Tinput>
        <Tinput change={operatorInfoChange} fieldName={'address'}>4-ADRESSE DE L’OPERATEUR DE SAISIE</Tinput>
        <Tinput change={operatorInfoChange} fieldName={'mobileNumber'}>5-N° DE GSM DE L’OPERATEUR DE SAISIE</Tinput>
        <Tinput change={operatorInfoChange} fieldName={'machineSerialNumber'}>6-N° DE SERIE DE LA MACHINE D’EMBOUTISSAGE</Tinput>
      </section> */}

      {/* <button onClick={submitForm}> submit </button> 
      {isLoading && <p>loading...</p>} */}
      <Button className="submit-button" onClick={() => { setStepper((cur) => cur + 1)}} variant="contained" color="primary">next</Button>
      <Button className="submit-button" onClick={() => { setStepper((cur) => cur + -1)}} variant="contained" color="primary">previous</Button>
      <Button className="submit-button" onClick={submitForm} variant="contained" color="primary">submit</Button>
    </main>
  );
}

export default Interface1;
