import React, { useRef } from 'react'
import { TextField } from '@mui/material';
const Tinput = ({children, change, fieldName, value }) => {
    const tval = useRef();
    return (
        <div className='t-input'>
            <TextField className="text-field" id="outlined-basic" value={tval.current.value} label={children} variant="outlined"  onChange={(e) => {
                const res = {};
                tval.current.value = e.target.value;
                res[fieldName] = e.target.value;
                change(res);
            }} />
        </div>
    )
}

export default Tinput
