import { useRef } from 'react';
import './App.css';
import Tinput from './components/Tinput';
// {
//  sileIndeCode: '',    
//  c

function App() {
  const data = useRef(null);



  function changeInput(value) {
    data.current = {...data.current, value};
    console.log(data.current);
  }

  return (
    <main> 
      <Tinput change={changeInput}>CODE D’IDENTIFICATION DU SITE N°</Tinput>
      <Tinput change={changeInput}>RAISON SOCIALE DU SITE</Tinput>
      <Tinput change={changeInput}>ADRESSE DU SITE</Tinput>
      <div className='bottom-flex'>
      <Tinput change={changeInput}>PATENTE N°</Tinput>
      <Tinput change={changeInput}>RC N°</Tinput>
      <Tinput change={changeInput}>ICE N°</Tinput>
      <Tinput change={changeInput}>ATEL</Tinput>
      </div>
    </main> 
  );
}

export default App;
