import { useRef, useState } from 'react';
import '../App.css';
import Tinput from '../components/Tinput';
import SectionTitle from '../components/SectionTitle';
import useSubmitForm from '../api/useSubmitForm';
import { Button } from '@mui/material';


function Interface1() {
  const [siteInfo, setSiteInfo] = useState({
    siteName: '',
    siteCode: '',
    siteAddress: '',
    sitePatente: '',
    siteRcNumber: '',
    siteIceNumber: '',
    siteAtel: '',
  });

  const [managerInfo, setManagerInfo] = useState({
    fullName: '',
    cineNumber: '',
    address: '',
    mobileNumber: '',
    email: ''
  });

  const [operatorInfo, setOperatorInfo] = useState({
    code: '',
    fullName: '',
    cineNumber: '',
    address: '',
    mobileNumber: '',
    machineSerialNumber: ''
  });

  const [stepper, setStepper] = useState(0); 
  const [submit, setSubmit] = useState({});
  const [res, isLoading, error] = useSubmitForm(submit);


  const siteInfoChange = (fieldName, value) => {
    setSiteInfo(prevState => ({...prevState, [fieldName]: value}));
    console.log(siteInfo)
  };

  function managerInfoChange(fieldName, value) {
    setManagerInfo(prevState => ({...prevState, [fieldName]: value}));
  }

  function operatorInfoChange(fieldName, value) {
    setOperatorInfo(prevState => ({...prevState, [fieldName]: value}));
  }

  function submitForm(e) {
    e.preventDefault();
    e.stopPropagation();
    setSubmit({ 
      siteSection: {...siteInfo},
      managerSection: {...managerInfo},
      operatorSection: {...operatorInfo}})
  }

  return (
    <main className="section1">
      { stepper === 0 && <section className='site-section'>
        <SectionTitle >INFORMATIONS SUR LE SITE</SectionTitle>
        <Tinput change={siteInfoChange} value={siteInfo.siteCode} fieldName={'siteCode'}>CODE D’IDENTIFICATION DU SITE N°</Tinput>
        <Tinput change={siteInfoChange} value={siteInfo.siteName} fieldName={'comName'}>RAISON SOCIALE DU SITE</Tinput>
        <Tinput change={siteInfoChange} value={siteInfo.siteAddress} fieldName={'siteAddress'}>ADRESSE DU SITE</Tinput>
        <div className='bottom-flex'>
          <Tinput change={siteInfoChange} value={siteInfo.sitePatente} fieldName={'patente'}>PATENTE N°</Tinput>
          <Tinput change={siteInfoChange} value={siteInfo.rcNumber} fieldName={'rcNumber'}> RC N°</Tinput>
          <Tinput change={siteInfoChange} value={siteInfo.iceNumber} fieldName={'iceNumber'}>ICE N°</Tinput>
          <Tinput change={siteInfoChange} value={siteInfo.atel} fieldName={'atel'}>ATEL</Tinput>
        </div>
      </section> 
      }

      { stepper === 1 && <section className="manager-section">
        <SectionTitle>INFORMATIONS SUR LE GERANT DU SITE</SectionTitle>
        <Tinput change={managerInfoChange} value={managerInfo.fullName} fieldName={'fullName'}>2-NOM ET PRENOM DU GERANT DU SITE</Tinput>
        <Tinput change={managerInfoChange} value={managerInfo.cineNumber} fieldName={'cineNumber'}>3-N° CINE DU GERANT DU SITE</Tinput>
        <Tinput change={managerInfoChange} value={managerInfo.address} fieldName={'address'}>4-ADRESSE DU GERANT DU SITE</Tinput>
        <Tinput change={managerInfoChange} value={managerInfo.mobileNumber} fieldName={'mobileNumber'}>5-N° DE GSM DU GERANT DU SITE</Tinput>
        <Tinput change={managerInfoChange} value={managerInfo.email} fieldName={'email'}>5-N° DE GSM DU GERANT DU SITE</Tinput>
      </section>
      }

      {stepper === 2 && <section className="operator-section">
        <SectionTitle>INFORMATIONS SUR L’OPERATEUR DE SAISIE</SectionTitle>
        <Tinput change={operatorInfoChange} value={operatorInfo.code} fieldName={'code'}>1-CODE OPERATEUR DE SAISIE</Tinput>
        <Tinput change={operatorInfoChange} value={operatorInfo.fullName} fieldName={'fullName'}>2-NOM ET PRENOM DE L’OPERATEUR DE SAISIE</Tinput>
        <Tinput change={operatorInfoChange} value={operatorInfo.cineNumber} fieldName={'cineNumber'}>3-N° CINE DE L’OPERATEUR DE SAISIE</Tinput>
        <Tinput change={operatorInfoChange} value={operatorInfo.address} fieldName={'address'}>4-ADRESSE DE L’OPERATEUR DE SAISIE</Tinput>
        <Tinput change={operatorInfoChange} value={operatorInfo.mobileNumber} fieldName={'mobileNumber'}>5-N° DE GSM DE L’OPERATEUR DE SAISIE</Tinput>
        <Tinput change={operatorInfoChange} value={operatorInfo.machineSerialNumber} fieldName={'machineSerialNumber'}>6-N° DE SERIE DE LA MACHINE D’EMBOUTISSAGE</Tinput>
      </section>}

      {/* <button onClick={submitForm}> submit </button> 
      {isLoading && <p>loading...</p>} */}
      {stepper !== 2 && <Button className="submit-button" onClick={() => { setStepper((cur) => cur < 2 ? cur + 1 : 2)}} variant="contained" color="primary">next</Button>}
      {stepper > 0 && <Button className="submit-button" onClick={() => { setStepper((cur) => cur > 0 ? cur - 1 : 0)}} variant="contained" color="primary">previous</Button>}
      {stepper === 2 && <Button className="submit-button" onClick={submitForm} variant="contained" color="primary">submit</Button>}
    </main>
  );
}

export default Interface1;
