import React, { useRef } from 'react'

const CheckBoxInput = ({children, field, change}) => {
    const ref = useRef({});

    return (
        <>
            <label>{children}</label>
            <input type="checkbox" name={field} ref={ref} onChange={() => {
                if (!change) return;
                const res = {};
                res[field] = ref.current.checked;
                change(res);
            }} />
        </>
    )
}

export default CheckBoxInput
