import React, { useRef } from 'react'
import { TextField } from '@mui/material';
const Tinput = ({children, change, fieldName, value}) => {
    const val = useRef({});
    return (
        <div className='t-input'>
            <TextField className="text-field" value={value} id="outlined-basic" label={children} variant="outlined" inputRef={val} onChange={() => {
                const res = {};
                res[fieldName] = val.current.value;

                change(res);
            }} />

        </div>
    )
}

export default Tinput
