import axios from 'axios'
import { useEffect, useState } from 'react';

function useSubmitForm(submited) {
  const [res, setRes] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);


  async function submit(source) {
    console.log(submited);
    if (Object.keys(submited).length === 0){
      console.log("object is empty");
      return;
    }
    try {
      setIsLoading(true);
      const { data } = await axios.post('http://localhost:5000/form-submition', submited, { cancelToken: source.token });
      setRes(data);
      console.log(data);
    }
    catch (e) {
      setError(true);
      console.log(e);
    }
    finally {
      setIsLoading(false);
    }
  }


  useEffect(() => {
    submit();

    return () => {
      source.cancel();
    }
  }, [submited])

  return [res, isLoading, error];
}

export default useSubmitForm;
