import React, { useRef, useState } from 'react'
import Tinput from '../components/Tinput';
import SectionTitle from '../components/SectionTitle';
import useSubmitForm from '../api/useSubmitForm';

// const VehicleOwnerInfo = {
//     LastName,
//     FirstName,
//     CINNumber,
//     VehicleRegistration,
//     OwnerAddress,
//     CINValidityDate,
// 	OwnerPhoneNumber, 
// 	OwnerEmail,
// }

// const conpanyInfo = {
//     RegistrationNumber,
//     CompanyName,   
//     CompanyAddress,
//     OwnerName,
//     OwnerCineNumber,
//     MandataryCineNumber,
//     OwnerPhoneNumber,
//     OwnerMobileNumber,
//     Email,
// }

const Interface3 = () => {
    const ownerInfo = useRef({});
    const companyInfo = useRef({});
    const [submited, setSubmited] = useState({});
    const [res, isLoading, error] = useSubmitForm(submited);

    const ownerInfoChange = (val) => { 
        ownerInfo.current = { ...ownerInfo.current, ...val };
        console.log(ownerInfo.current);
    }

    const companyInfoChange = (val) => {
        companyInfo.current = { ...companyInfo.current, ...val };
        console.log(companyInfo.current);
    }

    return (
        <>
            <section className='owner-section'>
                <SectionTitle> INFORMATIONS DU PROPRIETAIRE DU VEHICULE </SectionTitle>
                <Tinput change={ownerInfoChange} fieldName={'lastName'}>NOM</Tinput>
                <Tinput change={ownerInfoChange} fieldName={'firstName'}>PRENOM</Tinput>
                <Tinput change={ownerInfoChange} fieldName={'cineNumber'}>CINE N°</Tinput>
                <Tinput change={ownerInfoChange} fieldName={'ownerAddress'}>ADRESSE DU PROPRIETAIRE DU VEHICULE COMME INDIQUEE DANS LA CINE</Tinput>
                <Tinput change={ownerInfoChange} fieldName={'ownerCineValidationDate'}>DATE DE VALIDITE DE LA CINE DU PROPRIETAIRE DU VEHICULE</Tinput>
                <Tinput change={ownerInfoChange} fieldName={'ownerPhoneNumber'}>N° DE GSM DU PROPRIETAIRE DU VEHICULE</Tinput>
                <Tinput change={ownerInfoChange} fieldName={'ownerEmail'}>EMAIL DU PROPRIETAIRE DU VEHICULE</Tinput>
            </section>

            <section className='conpany-section'>
                <SectionTitle> INFORMATIONS DE LA SOCIETE </SectionTitle>
                <Tinput change={companyInfoChange} fieldName={'companyName'}>RAISON SOCIAL DE LA SOCIETE</Tinput>
                <Tinput change={companyInfoChange} fieldName={'rc'}>RC N°</Tinput>
                <Tinput change={companyInfoChange} fieldName={'address'}>ADRESSE</Tinput>
                <Tinput change={companyInfoChange} fieldName={'ownerCineNumber'}>NOM ET PRENOM DU PROPRIETAIRE</Tinput>
                <Tinput change={companyInfoChange} fieldName={'ownerCineNumber'}>CINE DU PROPRIETAIRE</Tinput>
                <Tinput change={companyInfoChange} fieldName={'mandatoryCineNumber'}>CINE DU MANDATAIRE</Tinput>
                <Tinput change={companyInfoChange} fieldName={'phoneNumber'}>TEL</Tinput>
                <Tinput change={companyInfoChange} fieldName={'email'}>EMAIL</Tinput>
            </section>

            <button onClick={() => setSubmited({ ownerinfo: {...ownerInfo.current},companyInfo: {...companyInfo.current} })}>Submit</button>
        </>
    )
}

export default Interface3
