import { useRef } from 'react';
import './App.css';
import Tinput from './components/Tinput';
// {
//  siteICode: '',    
//  companyName: '',
//  siteAddress: '',
//  patente: '',
//  rc: '',
//  ice: '',
//  atel: ''
// }

function App() {
  const data = useRef({});

  function changeInput(value) {
    data.current = {...data.current, ...value};
  }

  return (
    <main>
      <section> </section> 
      <Tinput change={changeInput} fieldName={'siteICode'}>CODE D’IDENTIFICATION DU SITE N°</Tinput>
      <Tinput change={changeInput} fieldName={'companyName'}>RAISON SOCIALE DU SITE</Tinput>
      <Tinput change={changeInput} fieldName={'siteAddress'}>ADRESSE DU SITE</Tinput>
      <div className='bottom-flex'>
      <Tinput change={changeInput} fieldName={'patente'}>PATENTE N°</Tinput>
      <Tinput change={changeInput} fieldName={'rc'}> RC N°</Tinput>
      <Tinput change={changeInput} fieldName={'ice'}>ICE N°</Tinput>
      <Tinput change={changeInput} fieldName={'atel'}>ATEL</Tinput>
      </div>


    </main> 
  );
}

export default App;
