import { useRef } from 'react';
import './App.css';
import Tinput from './components/Tinput';
// section1
// {
//  siteICode: '',    
//  companyName: '',
//  siteAddress: '',
//  patente: '',
//  rc: '',
//  ice: '',
//  atel: ''
// }

// section2
// {
//  managerFullName: '',
//  managerCine: '',
//  managerAddress: '',
//  managerPhone: '',
//  managergsm: '',
//  managerEmail: '',
// }



function App() {
  const section1 = useRef({});
  const section2 = useRef({});

  function section1Change(value) {
    section1.current = { ...section1.current, ...value };
  }

  function section2Change(value) {
    section2.current = { ...section2.current, ...value };
  }

  return (
    <main>
      <section className='section-1'>
        <Tinput change={section1Change} fieldName={'siteICode'}>CODE D’IDENTIFICATION DU SITE N°</Tinput>
        <Tinput change={section1Change} fieldName={'companyName'}>RAISON SOCIALE DU SITE</Tinput>
        <Tinput change={section1Change} fieldName={'siteAddress'}>ADRESSE DU SITE</Tinput>
        <div className='bottom-flex'>
          <Tinput change={section1Change} fieldName={'patente'}>PATENTE N°</Tinput>
          <Tinput change={section1Change} fieldName={'rc'}> RC N°</Tinput>
          <Tinput change={section1Change} fieldName={'ice'}>ICE N°</Tinput>
          <Tinput change={section1Change} fieldName={'atel'}>ATEL</Tinput>
        </div>
      </section>
      <section className="section-2">
          <Tinput change={section2Change} fieldName={'managerFullName'}>2-NOM ET PRENOM DU GERANT DU SITE</Tinput>
          <Tinput change={section2Change} fieldName={'managerFullName'}>3-N° CINE DU GERANT DU SITE</Tinput>
          <Tinput change={section2Change} fieldName={'managerFullName'}>4-ADRESSE DU GERANT DU SITE</Tinput>
          <Tinput change={section2Change} fieldName={'managerFullName'}>5-N° DE GSM DU GERANT DU SITE</Tinput>
      </section> 
    </main>
  );
}

export default App;
