import React, { useRef } from 'react'
import { TextField } from '@mui/material';
const Tinput = ({children, change, fieldName}) => {
    const val = useRef({});
    return (
        <div className='t-input'>
            <label htmlFor="">{children}</label>
            {/* <input type='text' ref={val} onChange={() => {
                const res = {};
                res[fieldName] = val.current.value;
                change(res);
            }}></input> */}
            <TextField id="outlined-basic" label={children} variant="outlined" ref={val} onChange={() => {
                const res = {};
                res[fieldName] = val.current.value;
                change(res);
            }} />

        </div>
    )
}

export default Tinput
