import { useRef } from 'react';
import './App.css';
import Tinput from './components/Tinput';

// const siteInfo = {
//   siteICode: '',      // 1. Site Identification Code Number
//   companyName: '',    // 2. Legal Name of the Site
//   siteAddress: '',     // 3. Site Address
//   patente: '',         // 4. Patent Number
//   rcNumber: '',       // 5. Commercial Registration (RC) Number
//   iceNumber: '',      // 6. Identification National Code for Companies (ICE) Number
//   atel: '',           // 7. ATEL
// };

// const managerInfo = {
//   fullName: '',      // 1. Manager's Full Name of the Site
//   cineNumber: '',    // 2. CINE Number of the Manager of the Site
//   address: '',       // 3. Address of the Manager of the Site
//   mobileNumber: '',  // 4. Mobile Number of the Manager of the Site
//   email: '',         // 5. Email of the Manager of the Site
// };


// const dataEntryOperatorInfo = {
//   code: '',                 // 1. Data Entry Operator Code
//   nameAndSurname: '',       // 2. Name and Surname of the Data Entry Operator
//   cineNumber: '',           // 3. CINE Number of the Data Entry Operator
//   address: '',              // 4. Address of the Data Entry Operator
//   mobileNumber: '',         // 5. Mobile Number of the Data Entry Operator
//   machineSerialNumber: '',  // 6. Serial Number of the Stamping Machine
// };


function App() {
  const siteInfo = useRef({});
  const managerInfo = useRef({});
  const operatorInfo = useRef({});

  function changeSiteInfo(value) {
    siteInfo.current = { ...siteInfo.current, ...value };
  }

  function managerInfoChange(value) {
    managerInfo.current = { ...managerInfo.current, ...value };
  }

  return (
    <main>
      <section className='section-1'>
        <Tinput change={changeSiteInfo} fieldName={'siteICode'}>CODE D’IDENTIFICATION DU SITE N°</Tinput>
        <Tinput change={changeSiteInfo} fieldName={'companyName'}>RAISON SOCIALE DU SITE</Tinput>
        <Tinput change={changeSiteInfo} fieldName={'siteAddress'}>ADRESSE DU SITE</Tinput>
        <div className='bottom-flex'>
          <Tinput change={changeSiteInfo} fieldName={'patente'}>PATENTE N°</Tinput>
          <Tinput change={changeSiteInfo} fieldName={'rcNumber'}> RC N°</Tinput>
          <Tinput change={changeSiteInfo} fieldName={'iceNumber'}>ICE N°</Tinput>
          <Tinput change={changeSiteInfo} fieldName={'atel'}>ATEL</Tinput>
        </div>
      </section>
      <section className="manager-section">
          <Tinput change={section2Change} fieldName={'fullName'}>2-NOM ET PRENOM DU GERANT DU SITE</Tinput>
          <Tinput change={section2Change} fieldName={'cineNumber'}>3-N° CINE DU GERANT DU SITE</Tinput>
          <Tinput change={section2Change} fieldName={'address'}>4-ADRESSE DU GERANT DU SITE</Tinput>
          <Tinput change={section2Change} fieldName={'mobileNumber'}>5-N° DE GSM DU GERANT DU SITE</Tinput>
          <Tinput change={section2Change} fieldName={'email'}>5-N° DE GSM DU GERANT DU SITE</Tinput>
      </section> 

      <section className="section-3">
      <Tinput change={section2Change} fieldName={'managerEmail'}>1-CODE OPERATEUR DE SAISIE</Tinput>
      <Tinput change={section2Change} fieldName={'managerEmail'}>2-NOM ET PRENOM DE L’OPERATEUR DE SAISIE</Tinput>
      <Tinput change={section2Change} fieldName={'managerEmail'}>3-N° CINE DE L’OPERATEUR DE SAISIE</Tinput>
      <Tinput change={section2Change} fieldName={'managerEmail'}>4-ADRESSE DE L’OPERATEUR DE SAISIE</Tinput>
      <Tinput change={section2Change} fieldName={'managerEmail'}>5-N° DE GSM DE L’OPERATEUR DE SAISIE</Tinput>
      <Tinput change={section2Change} fieldName={'managerEmail'}>6-N° DE SERIE DE LA MACHINE D’EMBOUTISSAGE</Tinput>

      </section>
    </main>
  );
}

export default App;
